// get node postgres connector
import pg from 'pg';
import debug from 'debug';
import configuration from '../config/config'

const env = process.env.NODE_ENV ? process.env.NODE_ENV : 'development';

//const config = configuration[env];

console.log('this is the environment: ', env.toString() );
console.log(env === 'test');


if (env === 'production') {
const pool = pg.Pool(configuration.production);
console.log(1);
pool.on('connect', () => {
  debug('app:database')('connected to the Database');
  module.exports = pool;
});
} else if (env === 'test') {
  const pool = pg.Pool(configuration.test);
  console.log(2);
  pool.on('connect', () => {
    debug('app:database')('connected to the Database');
  });
  module.exports = pool;
} else {
  const pool = pg.Pool(configuration.development);
  console.log(3);
  pool.on('connect', () => {
    debug('app:database')('connected to the Database');
  });
  module.exports = pool;
}

